import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Workshop } from '../models/workshop';
import { WorkshopService } from '../workshop.service';

@Component({
  selector: 'app-become-organizator',
  templateUrl: './become-organizator.component.html',
  styleUrls: ['./become-organizator.component.css']
})
export class BecomeOrganizatorComponent implements OnInit {

  constructor(private workshopService: WorkshopService, private router: Router) { }

  ngOnInit(): void {
  }
  imageToUpload: File;
  galleryImagesToUpload: File[];
  imageErr: string;
  name_of_work: string;
  place: string;
  latitude: string;
  longtitude: string;
  date: Date;
  shortDesc: string;
  longDesc: string;
  numOfSpaces: string;
  uploadImage(files: FileList):void{
    this.imageToUpload = files.item(0);
    let image = new Image()
    image.src = window.URL.createObjectURL(this.imageToUpload)
    image.onload = () => {
      if(image.width < 100 || image.height < 100 || image.width > 300 || image.height > 300){
        this.imageErr = '0';
      } else {
        this.imageErr = '2';

      }                
    }
  }
  sendWorkshopRequest(){
    let formData = new FormData();
    //formData.append('main_picture', this.imageToUpload);
    // for(let file of this.galleryImagesToUpload){
    //   formData.append('gallery_pics', file);
    // }
    formData.append('workshopName', this.name_of_work);
    formData.append('date', this.date.toString());
    formData.append('shortDesc', this.shortDesc);
    formData.append('longDesc', this.longDesc);
    formData.append('lat', this.latitude);
    formData.append('long', this.longtitude);
    formData.append('place', this.place);
    formData.append('numOfSpaces', this.numOfSpaces);
    formData.append('username', sessionStorage.getItem('username'))
    formData.append('main_picture', this.imageToUpload);
    for(let f = 0; f < this.galleryImagesToUpload.length; f++){
      formData.append('gallery_pics', this.galleryImagesToUpload[f]);
    }
    this.workshopService.suggestWorkshop(formData).subscribe((resp)=>{
        console.log(resp)
    })
  }
  uploadImages(files: FileList):void{
    this.galleryImagesToUpload = [];
    for(let i = 0; i < files.length; i++){
      this.galleryImagesToUpload[i] = files.item(i);
      // let image = new Image();
      // image.src = window.URL.createObjectURL(this.galleryImagesToUpload[i]);
    }
  }
  changePass(){
    this.router.navigate(['change-password']);
  }
  logout(){
    sessionStorage.clear();
    this.router.navigate(['']);
  }
}
<mat-sidenav-container class="example-container">
    <mat-sidenav mode="side" opened>
        <p>
            <button routerLink="/user-info">Profile</button>
        </p>
        <p>
            <button routerLink="/user-workshops">Workshops</button>
        </p>
        <p>
            <button routerLink="/become-organizer">Become organizer</button>
        </p>

    </mat-sidenav>
    <mat-sidenav-content>
        <table>
            <tr>
                <td>
                    Picture:
                </td>
                <td>
                    <input name="main_picture" type="file" (change)="uploadImage($any($event).target.files)">
                </td>
            </tr>
            <tr>
                <td>
                    Name:
                </td>
                <td>
                    <input type="text" name="name_of_work" [(ngModel)]="name_of_work">
                </td>
            </tr>
            <tr>
                <td>
                    Place:
                </td>
                <td>
                    <input type="text" name="place" [(ngModel)]="place">
                </td>
            </tr>
            <tr>
                <td>
                    Coordinates(optional):
                </td>
                <td>
                  <table>
                    <tr>
                        <td>
                            Lat:
                        </td>
                        <td>
                            <input type="text" name="latitude" [(ngModel)]="latitude">
                        </td>
                    </tr>
                    <tr>
                        <td>
                            Long:
                        </td>
                        <td>
                            <input type="text" name="longtitude" [(ngModel)]="longtitude">
                        </td>
                    </tr>
                  </table>  
                </td>
            </tr>
            <tr>
                <td>
                    Date:
                </td>
                <td>
                    <input type="date" name="date" [(ngModel)]="date">
                </td>
            </tr>
            <tr>
                <td>
                    Number of spaces:
                </td>
                <td>
                    <input type="text" name="numOfSpaces" [(ngModel)]="numOfSpaces">
                </td>
            </tr>
            <tr>
                <td>
                    Short description:
                </td>
                <td>
                    <input type="text" name="shortDesc" [(ngModel)]="shortDesc">
                </td>
            </tr>
            <tr>
                <td>
                    Long description:
                </td>
                <td>
                    <input type="text" name="longDesc" [(ngModel)]="longDesc">
                </td>
            </tr>
            <tr>
                <td>
                    Gallery pics:
                </td>
                <td>
                    <input name="gallery_pics" type="file" (change)="uploadImages($any($event).target.files)" multiple>
                </td>
            </tr>
            <tr>
                <td colspan="2">
                    <button (click)="sendWorkshopRequest()">Submit</button>
                </td>
            </tr>
        </table>
        <footer>
            <form>
                <button (click)="changePass()">Change password</button>
            </form>
            <form>
                <button (click)="logout()">Logout</button>
            </form>
        </footer>
    </mat-sidenav-content>
  </mat-sidenav-container>
