            <input type="text" name="latEdit" [(ngModel)]="latEdit">
        </td>
    </tr> 
    <tr>
        <td>
            Workshop long:
        </td>
        <td>
            {{workshop.long}}
        </td>
        <td>
            <input type="text" name="longEdit" [(ngModel)]="longEdit">
        </td>
    </tr> 
    <tr>
        <td colspan="3">
            <button (click)="edit()">Edit</button>
        </td>
    </tr>

