import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Workshop } from '../models/workshop';
import { WorkshopService } from '../workshop.service';

@Component({
  selector: 'app-edit-workshop',
  templateUrl: './edit-workshop.component.html',
  styleUrls: ['./edit-workshop.component.css']
})
export class EditWorkshopComponent implements OnInit {

  constructor(private workshopService : WorkshopService, private router: Router, private route: ActivatedRoute) { }

  workshopOrganizer : string;
  workshopId : string;
  workshop : Workshop;
  workshopName : string;
  workshopDate : Date;
  workshopPlace : string;
  workshopDesc : string;
  workshopImage: string;
  lat : number;
  long : number;


  workshopNameEdit : string;
  workshopDateEdit : Date;
  workshopPlaceEdit : string;
  workshopDescEdit : string;
  workshopImageEdit: string;
  latEdit : number;
  longEdit : number;


  edit(){

  }
  ngOnInit(): void {
    this.workshopId = this.route.snapshot.paramMap.get('workshopId');

    this.workshopDesc = this.route.snapshot.paramMap.get('workshopDesc');
    this.workshopName = this.route.snapshot.paramMap.get('workshopName');
    this.workshopService.getWorkshopById(this.workshopId).subscribe((resp)=>{
      this.workshop = resp as Workshop;
      this.workshopOrganizer = this.workshop.organizatorUsername;
    })
  }

}
<table>
    <p>{{workshopOrganizer}}</p>
    <p>{{workshopId}}</p>
    <tr>
        <td>
            Workshop name:
        </td>
        <td>
            {{workshop.workshopName}}
        </td>
        <td>
            <input type="text" name="workshopName" [(ngModel)]="workshopName">
        </td>
    </tr>  
    <tr>
        <td>
            Workshop date:
        </td>
        <td>
            {{workshop.workshopDate}}
        </td>
        <td>
            <input type="date" name="workshopDate" [(ngModel)]="workshopDate">
        </td>
    </tr> 
    <tr>
        <td>
            Workshop image:
        </td>
        <td>
            {{workshop.workshopImage}}
        </td>
        <td>
            <input type="text" name="workshopImage" [(ngModel)]="workshopImage">
        </td>
    </tr> 
    <tr>
        <td>
            Workshop place:
        </td>
        <td>
            {{workshop.workshopPlace}}
        </td>
        <td>
            <input type="text" name="workshopPlace" [(ngModel)]="workshopPlace">
        </td>
    </tr> 
    <tr>
        <td>
            Workshop desc:
        </td>
        <td>
            {{workshop.workshopDesc}}
        </td>
        <td>
            <input type="text" name="workshopDesc" [(ngModel)]="workshopDesc">
        </td>
    </tr> 
    <tr>
        <td>
            Workshop lat:
        </td>
        <td>
            {{workshop.lat}}
        </td>
        <td>
            <input type="text" name="lat" [(ngModel)]="lat">
        </td>
    </tr> 
    <tr>
        <td>
            Workshop long:
        </td>
        <td>
            {{workshop.long}}
        </td>
        <td>
            <input type="text" name="long" [(ngModel)]="long">
        </td>
    </tr> 
    <tr>
        <td colspan="3">
            <button (click)="edit()">Edit</button>
        </td>
    </tr>

</table>
